import bisect


class Solution(object):
    def numMatchingSubseq(self, S, words):
        """
        :type S: str
        :type words: List[str]
        :rtype: int
        """
        pos = {}
        for i, c in enumerate(S):
            if c not in pos:
                pos[c] = []
            pos[c].append(i)
        count = 0
        for word in words:
            cur = -1
            valid = True
            for c in word:
                if c not in pos:
                    valid = False
                    break
                idx = bisect.bisect_right(pos[c], cur)
                if idx > len(pos[c]) - 1:
                    valid = False
                    break
                else:
                    cur = pos[c][idx]
            if valid:
                count += 1
        return count


